import React, {useState} from 'react';
import LocationCard from '../LocationCard/LocationCard';
import Header from '../Header/Header';
import axios from 'axios';
import './mapPage.scss';
import SortBar from '../SortBar/SortBar';
import LocationFoot from '../LocationFoot/LocationFoot';

function MapPage (props) {
    let location = props.state.locations.locations;

    const [sorted, setSorted] = useState();

    const [portraitList, setPortraitList] = useState();
    const sortPortraits = (event) => {
        if (props.state.locations > 0) {
            let newPortraitList = props.state.locations.filter(location => location.categories.find('portraits'))
            setPortraitList(newPortraitList);
            setSorted('portraits');
        }
    }

    const [weddingList, setWeddingList] = useState();
    const sortWeddings = (event) => {
        if (props.state.locations > 0) {
            let newWeddingList = props.state.locations.filter(location => location.categories.find('wedding'))
            setPortraitList(newWeddingList);
            setSorted('wedding');
        }
    }

    const [lifestyleList, setLifestyleList] = useState();
    const sortLifestyle = (event) => {
        if (props.state.locations > 0) {
            let newLifestyleList = props.state.locations.filter(location => location.categories.find('lifestyle'))
            setLifestyleList(newLifestyleList);
            setSorted('lifestyle');
        }
    }

    const [engagementList, setEngagementList] = useState();
    const sortEngagement = (event) => {
        if (props.state.locations > 0) {
            let newEngagementList = props.state.locations.filter(location => location.categories.find('engagements'))
            setPortraitList(newEngagementList);
            setSorted('engagements');
        }
    }

    return (
        <>
            <Header />
            <SortBar sortPortraits={this.sortPortraits} sortEngagements={this.sortEngagements} sortLifestyle={this.sortLifestyle} sortWeddings={this.sortWeddings}/>
            <div className="Cards">
                {location.map(location => 
                    <LocationCard 
                    location={location.location}
                    description={location.description}
                    img={location.img}
                    id={location.id}
                    key={location.id}
                    />
                )}
            </div>
            <LocationFoot />
        </>
    )
}

    // for more info clicked through from a specific card it will change dynamic url to /locations/:id 

export default MapPage;
