import React, { useState } from 'react';
import ReactMapGL, { Marker } from 'react-map-gl';
import './mapbox.scss';

export default function MapBox(props) {
    const {location} = props.location;
    console.log(location)

    const [viewport, setViewport] = useState({
        width: '85vw',
        height: 400,
        latitude: 43.637371,
        longitude: -79.457981,
        zoom: 12,
      });

    return (

        <ReactMapGL className="mapBox" {...viewport} mapboxApiAccessToken={'pk.eyJ1Ijoic2VhbnBvbGxvY2siLCJhIjoiY2toZHV5MnRkMGM0ajJ4cGoxeGJjeHdmaCJ9.s6rqIVazeUxiBYVry-xefQ'}
        onViewportChange={viewport => {
            setViewport(viewport);
        }}
        mapStyle="mapbox://styles/seanpollock/ckhdwl9cm03qt1aqtm1ot9myc">
            {location !== undefined && <Marker latitude={43.637371} longitude={-79.457981}>
                <div>Location</div>
            </Marker>}
        </ReactMapGL>
    )
}
